mapper.select:

select * from(
	select rownum as rn, bno, rno, reply, replyer, replyDate, updateDate
	FROM(
		select /*+INDEX_DESC(tbl_reply rno)*/
			rownum as rn, bno, rno, reply, replyer, replyDate, updateDate
		from tbl_reply
		where bno = #{bno}
		ORDER BY rno DESC
		)
	where rownum <= #{criteria.maxRow} 
) 
where rn > #{criteria.minRow}
